import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as p,e as c}from"./app.db8d92f8.js";const d={},l=c('<h1 id="_5-\u89C6\u56FE" tabindex="-1"><a class="header-anchor" href="#_5-\u89C6\u56FE" aria-hidden="true">#</a> 5. \u89C6\u56FE</h1><h2 id="\u89C6\u56FE\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u89C6\u56FE\u6982\u8FF0" aria-hidden="true">#</a> \u89C6\u56FE\u6982\u8FF0</h2><p>MySQL \u89C6\u56FE\uFF08View\uFF09\u662F\u4E00\u79CD\u865A\u62DF\u5B58\u5728\u7684\u8868\uFF0C\u540C\u771F\u5B9E\u8868\u4E00\u6837\uFF0C\u89C6\u56FE\u4E5F\u7531\u5217\u548C\u884C\u6784\u6210\uFF0C\u4F46\u89C6\u56FE\u5E76\u4E0D\u5B9E\u9645\u5B58\u5728\u4E8E\u6570\u636E\u5E93\u4E2D\u3002\u884C\u548C\u5217\u7684\u6570\u636E\u6765\u81EA\u4E8E\u5B9A\u4E49\u89C6\u56FE\u7684\u67E5\u8BE2\u4E2D\u6240\u4F7F\u7528\u7684\u8868\uFF0C\u5E76\u4E14\u8FD8\u662F\u5728\u4F7F\u7528\u89C6\u56FE\u65F6\u52A8\u6001\u751F\u6210\u7684\u3002</p><p>\u6570\u636E\u5E93\u4E2D\u53EA\u5B58\u653E\u4E86\u89C6\u56FE\u7684\u5B9A\u4E49\uFF0C\u5E76\u6CA1\u6709\u5B58\u653E\u89C6\u56FE\u4E2D\u7684\u6570\u636E\uFF0C\u8FD9\u4E9B\u6570\u636E\u90FD\u5B58\u653E\u5728\u5B9A\u4E49\u89C6\u56FE\u67E5\u8BE2\u6240\u5F15\u7528\u7684\u771F\u5B9E\u8868\u4E2D\u3002\u4F7F\u7528\u89C6\u56FE\u67E5\u8BE2\u6570\u636E\u65F6\uFF0C\u6570\u636E\u5E93\u4F1A\u4ECE\u771F\u5B9E\u8868\u4E2D\u53D6\u51FA\u5BF9\u5E94\u7684\u6570\u636E\u3002\u56E0\u6B64\uFF0C\u89C6\u56FE\u4E2D\u7684\u6570\u636E\u662F\u4F9D\u8D56\u4E8E\u771F\u5B9E\u8868\u4E2D\u7684\u6570\u636E\u7684\u3002\u4E00\u65E6\u771F\u5B9E\u8868\u4E2D\u7684\u6570\u636E\u53D1\u751F\u6539\u53D8\uFF0C\u663E\u793A\u5728\u89C6\u56FE\u4E2D\u7684\u6570\u636E\u4E5F\u4F1A\u53D1\u751F\u6539\u53D8\u3002</p><p>\u89C6\u56FE\u53EF\u4EE5\u4ECE\u539F\u6709\u7684\u8868\u4E0A\u9009\u53D6\u5BF9\u7528\u6237\u6709\u7528\u7684\u4FE1\u606F\uFF0C\u90A3\u4E9B\u5BF9\u7528\u6237\u6CA1\u7528\uFF0C\u6216\u8005\u7528\u6237\u6CA1\u6709\u6743\u9650\u4E86\u89E3\u7684\u4FE1\u606F\uFF0C\u90FD\u53EF\u4EE5\u76F4\u63A5\u5C4F\u853D\u6389\uFF0C\u4F5C\u7528\u7C7B\u4F3C\u4E8E\u7B5B\u9009\u3002\u8FD9\u6837\u505A\u65E2\u4F7F\u5E94\u7528\u7B80\u5355\u5316\uFF0C\u4E5F\u4FDD\u8BC1\u4E86\u7CFB\u7EDF\u7684\u5B89\u5168\u3002</p><p>MySQL \u7684\u89C6\u56FE\u4E0D\u652F\u6301\u8F93\u5165\u53C2\u6570\u7684\u529F\u80FD\uFF0C\u56E0\u6B64\u4EA4\u4E92\u6027\u4E0A\u8FD8\u6709\u6B20\u7F3A\u3002\u4F46\u5BF9\u4E8E\u53D8\u5316\u4E0D\u662F\u5F88\u5927\u7684\u64CD\u4F5C\uFF0C\u4F7F\u7528\u89C6\u56FE\u53EF\u4EE5\u5F88\u5927\u7A0B\u5EA6\u4E0A\u7B80\u5316\u7528\u6237\u7684\u64CD\u4F5C\u3002</p><p>\u89C6\u56FE\u5E76\u4E0D\u540C\u4E8E\u6570\u636E\u8868\uFF0C\u5B83\u4EEC\u7684\u533A\u522B\u5728\u4E8E\u4EE5\u4E0B\u51E0\u70B9\uFF1A</p><ul><li>\u89C6\u56FE\u4E0D\u662F\u6570\u636E\u5E93\u4E2D\u771F\u5B9E\u7684\u8868\uFF0C\u800C\u662F\u4E00\u5F20\u865A\u62DF\u8868\uFF0C\u5176\u7ED3\u6784\u548C\u6570\u636E\u662F\u5EFA\u7ACB\u5728\u5BF9\u6570\u636E\u4E2D\u771F\u5B9E\u8868\u7684\u67E5\u8BE2\u57FA\u7840\u4E0A\u7684\u3002</li><li>\u5B58\u50A8\u5728\u6570\u636E\u5E93\u4E2D\u7684\u67E5\u8BE2\u64CD\u4F5C SQL \u8BED\u53E5\u5B9A\u4E49\u4E86\u89C6\u56FE\u7684\u5185\u5BB9\uFF0C\u5217\u6570\u636E\u548C\u884C\u6570\u636E\u6765\u81EA\u4E8E\u89C6\u56FE\u67E5\u8BE2\u6240\u5F15\u7528\u7684\u5B9E\u9645\u8868\uFF0C\u5F15\u7528\u89C6\u56FE\u65F6\u52A8\u6001\u751F\u6210\u8FD9\u4E9B\u6570\u636E\u3002</li><li>\u89C6\u56FE\u6CA1\u6709\u5B9E\u9645\u7684\u7269\u7406\u8BB0\u5F55\uFF0C\u4E0D\u662F\u4EE5\u6570\u636E\u96C6\u7684\u5F62\u5F0F\u5B58\u50A8\u5728\u6570\u636E\u5E93\u4E2D\u7684\uFF0C\u5B83\u6240\u5BF9\u5E94\u7684\u6570\u636E\u5B9E\u9645\u4E0A\u662F\u5B58\u50A8\u5728\u89C6\u56FE\u6240\u5F15\u7528\u7684\u771F\u5B9E\u8868\u4E2D\u7684\u3002</li><li>\u89C6\u56FE\u662F\u6570\u636E\u7684\u7A97\u53E3\uFF0C\u800C\u8868\u662F\u5185\u5BB9\u3002\u8868\u662F\u5B9E\u9645\u6570\u636E\u7684\u5B58\u653E\u5355\u4F4D\uFF0C\u800C\u89C6\u56FE\u53EA\u662F\u4EE5\u4E0D\u540C\u7684\u663E\u793A\u65B9\u5F0F\u5C55\u793A\u6570\u636E\uFF0C\u5176\u6570\u636E\u6765\u6E90\u8FD8\u662F\u5B9E\u9645\u8868\u3002</li><li>\u89C6\u56FE\u662F\u67E5\u770B\u6570\u636E\u8868\u7684\u4E00\u79CD\u65B9\u6CD5\uFF0C\u53EF\u4EE5\u67E5\u8BE2\u6570\u636E\u8868\u4E2D\u67D0\u4E9B\u5B57\u6BB5\u6784\u6210\u7684\u6570\u636E\uFF0C\u53EA\u662F\u4E00\u4E9B SQL \u8BED\u53E5\u7684\u96C6\u5408\u3002\u4ECE\u5B89\u5168\u7684\u89D2\u5EA6\u6765\u770B\uFF0C\u89C6\u56FE\u7684\u6570\u636E\u5B89\u5168\u6027\u66F4\u9AD8\uFF0C\u4F7F\u7528\u89C6\u56FE\u7684\u7528\u6237\u4E0D\u63A5\u89E6\u6570\u636E\u8868\uFF0C\u4E0D\u77E5\u9053\u8868\u7ED3\u6784\u3002</li><li>\u89C6\u56FE\u7684\u5EFA\u7ACB\u548C\u5220\u9664\u53EA\u5F71\u54CD\u89C6\u56FE\u672C\u8EAB\uFF0C\u4E0D\u5F71\u54CD\u5BF9\u5E94\u7684\u57FA\u672C\u8868\u3002</li></ul><p><strong>\u89C6\u56FE\u7684\u4F18\u70B9\uFF1A</strong></p><p>\u89C6\u56FE\u4E0E\u8868\u5728\u672C\u8D28\u4E0A\u867D\u7136\u4E0D\u76F8\u540C\uFF0C\u4F46\u89C6\u56FE\u7ECF\u8FC7\u5B9A\u4E49\u4EE5\u540E\uFF0C\u7ED3\u6784\u5F62\u5F0F\u548C\u8868\u4E00\u6837\uFF0C\u53EF\u4EE5\u8FDB\u884C\u67E5\u8BE2\u3001\u4FEE\u6539\u3001\u66F4\u65B0\u548C\u5220\u9664\u7B49\u64CD\u4F5C\u3002\u540C\u65F6\uFF0C\u89C6\u56FE\u5177\u6709\u5982\u4E0B\u4F18\u70B9\uFF1A</p><ol><li><p>\u5B9A\u5236\u7528\u6237\u6570\u636E\uFF0C\u805A\u7126\u7279\u5B9A\u7684\u6570\u636E</p><p>\u5728\u5B9E\u9645\u7684\u5E94\u7528\u8FC7\u7A0B\u4E2D\uFF0C\u4E0D\u540C\u7684\u7528\u6237\u53EF\u80FD\u5BF9\u4E0D\u540C\u7684\u6570\u636E\u6709\u4E0D\u540C\u7684\u8981\u6C42\u3002</p><p>\u4F8B\u5982\uFF0C\u5F53\u6570\u636E\u5E93\u540C\u65F6\u5B58\u5728\u65F6\uFF0C\u5982\u5B66\u751F\u57FA\u672C\u4FE1\u606F\u8868\u3001\u8BFE\u7A0B\u8868\u548C\u6559\u5E08\u4FE1\u606F\u8868\u7B49\u591A\u79CD\u8868\u540C\u65F6\u5B58\u5728\u65F6\uFF0C\u53EF\u4EE5\u6839\u636E\u9700\u6C42\u8BA9\u4E0D\u540C\u7684\u7528\u6237\u4F7F\u7528\u5404\u81EA\u7684\u6570\u636E\u3002</p><p>\u5B66\u751F\u67E5\u770B\u4FEE\u6539\u81EA\u5DF1\u57FA\u672C\u4FE1\u606F\u7684\u89C6\u56FE\uFF0C\u5B89\u6392\u8BFE\u7A0B\u4EBA\u5458\u67E5\u770B\u4FEE\u6539\u8BFE\u7A0B\u8868\u548C\u6559\u5E08\u4FE1\u606F\u7684\u89C6\u56FE\uFF0C\u6559\u5E08\u67E5\u770B\u5B66\u751F\u4FE1\u606F\u548C\u8BFE\u7A0B\u4FE1\u606F\u8868\u7684\u89C6\u56FE\u3002</p></li><li><p>\u7B80\u5316\u6570\u636E\u64CD\u4F5C</p><p>\u5728\u4F7F\u7528\u67E5\u8BE2\u65F6\uFF0C\u5F88\u591A\u65F6\u5019\u8981\u4F7F\u7528\u805A\u5408\u51FD\u6570\uFF0C\u540C\u65F6\u8FD8\u8981\u663E\u793A\u5176\u4ED6\u5B57\u6BB5\u7684\u4FE1\u606F\uFF0C\u53EF\u80FD\u8FD8\u9700\u8981\u5173\u8054\u5230\u5176\u4ED6\u8868\uFF0C\u8BED\u53E5\u53EF\u80FD\u4F1A\u5F88\u957F\uFF0C\u5982\u679C\u8FD9\u4E2A\u52A8\u4F5C\u9891\u7E41\u53D1\u751F\u7684\u8BDD\uFF0C\u53EF\u4EE5\u521B\u5EFA\u89C6\u56FE\u6765\u7B80\u5316\u64CD\u4F5C\u3002</p></li><li><p>\u63D0\u9AD8\u6570\u636E\u7684\u5B89\u5168\u6027</p><p>\u89C6\u56FE\u662F\u865A\u62DF\u7684\uFF0C\u7269\u7406\u4E0A\u662F\u4E0D\u5B58\u5728\u7684\u3002\u53EF\u4EE5\u53EA\u6388\u4E88\u7528\u6237\u89C6\u56FE\u7684\u6743\u9650\uFF0C\u800C\u4E0D\u5177\u4F53\u6307\u5B9A\u4F7F\u7528\u8868\u7684\u6743\u9650\uFF0C\u6765\u4FDD\u62A4\u57FA\u7840\u6570\u636E\u7684\u5B89\u5168\u3002</p></li><li><p>\u5171\u4EAB\u6240\u9700\u6570\u636E</p><p>\u901A\u8FC7\u4F7F\u7528\u89C6\u56FE\uFF0C\u6BCF\u4E2A\u7528\u6237\u4E0D\u5FC5\u90FD\u5B9A\u4E49\u548C\u5B58\u50A8\u81EA\u5DF1\u6240\u9700\u7684\u6570\u636E\uFF0C\u53EF\u4EE5\u5171\u4EAB\u6570\u636E\u5E93\u4E2D\u7684\u6570\u636E\uFF0C\u540C\u6837\u7684\u6570\u636E\u53EA\u9700\u8981\u5B58\u50A8\u4E00\u6B21\u3002</p></li><li><p>\u66F4\u6539\u6570\u636E\u683C\u5F0F</p><p>\u901A\u8FC7\u4F7F\u7528\u89C6\u56FE\uFF0C\u53EF\u4EE5\u91CD\u65B0\u683C\u5F0F\u5316\u68C0\u7D22\u51FA\u7684\u6570\u636E\uFF0C\u5E76\u7EC4\u7EC7\u8F93\u51FA\u5230\u5176\u4ED6\u5E94\u7528\u7A0B\u5E8F\u4E2D\u3002</p></li><li><p>\u91CD\u7528 SQL \u8BED\u53E5</p><p>\u89C6\u56FE\u63D0\u4F9B\u7684\u662F\u5BF9\u67E5\u8BE2\u64CD\u4F5C\u7684\u5C01\u88C5\uFF0C\u672C\u8EAB\u4E0D\u5305\u542B\u6570\u636E\uFF0C\u6240\u5448\u73B0\u7684\u6570\u636E\u662F\u6839\u636E\u89C6\u56FE\u5B9A\u4E49\u4ECE\u57FA\u7840\u8868\u4E2D\u68C0\u7D22\u51FA\u6765\u7684\uFF0C\u5982\u679C\u57FA\u7840\u8868\u7684\u6570\u636E\u65B0\u589E\u6216\u5220\u9664\uFF0C\u89C6\u56FE\u5448\u73B0\u7684\u4E5F\u662F\u66F4\u65B0\u540E\u7684\u6570\u636E\u3002\u89C6\u56FE\u5B9A\u4E49\u540E\uFF0C\u7F16\u5199\u5B8C\u6240\u9700\u7684\u67E5\u8BE2\uFF0C\u53EF\u4EE5\u65B9\u4FBF\u5730\u91CD\u7528\u8BE5\u89C6\u56FE\u3002</p></li></ol><p>\u8981\u6CE8\u610F\u533A\u522B\u89C6\u56FE\u548C\u6570\u636E\u8868\u7684\u672C\u8D28\uFF0C\u5373\u89C6\u56FE\u662F\u57FA\u4E8E\u771F\u5B9E\u8868\u7684\u4E00\u5F20\u865A\u62DF\u7684\u8868\uFF0C\u5176\u6570\u636E\u6765\u6E90\u5747\u5EFA\u7ACB\u5728\u771F\u5B9E\u8868\u7684\u57FA\u7840\u4E0A\u3002</p><p>\u4F7F\u7528\u89C6\u56FE\u7684\u65F6\u5019\uFF0C\u8FD8\u5E94\u8BE5\u6CE8\u610F\u4EE5\u4E0B\u51E0\u70B9\uFF1A</p><ul><li><p>\u521B\u5EFA\u89C6\u56FE\u9700\u8981\u8DB3\u591F\u7684\u8BBF\u95EE\u6743\u9650\u3002</p></li><li><p>\u521B\u5EFA\u89C6\u56FE\u7684\u6570\u76EE\u6CA1\u6709\u9650\u5236\u3002</p></li><li><p>\u89C6\u56FE\u53EF\u4EE5\u5D4C\u5957\uFF0C\u5373\u4ECE\u5176\u4ED6\u89C6\u56FE\u4E2D\u68C0\u7D22\u6570\u636E\u7684\u67E5\u8BE2\u6765\u521B\u5EFA\u89C6\u56FE\u3002</p></li><li><p>\u89C6\u56FE\u4E0D\u80FD\u7D22\u5F15\uFF0C\u4E5F\u4E0D\u80FD\u6709\u5173\u8054\u7684\u89E6\u53D1\u5668\u3001\u9ED8\u8BA4\u503C\u6216\u89C4\u5219\u3002</p></li><li><p>\u89C6\u56FE\u53EF\u4EE5\u548C\u8868\u4E00\u8D77\u4F7F\u7528\u3002</p></li><li><p>\u89C6\u56FE\u4E0D\u5305\u542B\u6570\u636E\uFF0C\u6240\u4EE5\u6BCF\u6B21\u4F7F\u7528\u89C6\u56FE\u65F6\uFF0C\u90FD\u5FC5\u987B\u6267\u884C\u67E5\u8BE2\u4E2D\u6240\u9700\u7684\u4EFB\u4F55\u4E00\u4E2A\u68C0\u7D22\u64CD\u4F5C\u3002\u5982\u679C\u7528\u591A\u4E2A\u8FDE\u63A5\u548C\u8FC7\u6EE4\u6761\u4EF6\u521B\u5EFA\u4E86\u590D\u6742\u7684\u89C6\u56FE\u6216\u5D4C\u5957\u4E86\u89C6\u56FE\uFF0C\u53EF\u80FD\u4F1A\u53D1\u73B0\u7CFB\u7EDF\u8FD0\u884C\u6027\u80FD\u4E0B\u964D\u5F97\u5341\u5206\u4E25\u91CD\u3002\u56E0\u6B64\uFF0C\u5728\u90E8\u7F72\u5927\u91CF\u89C6\u56FE\u5E94\u7528\u65F6\uFF0C\u5E94\u8BE5\u8FDB\u884C\u7CFB\u7EDF\u6D4B\u8BD5\u3002</p></li></ul><p>\u63D0\u793A\uFF1A<code>ORDER BY</code> \u5B50\u53E5\u53EF\u4EE5\u7528\u5728\u89C6\u56FE\u4E2D\uFF0C\u4F46\u82E5\u8BE5\u89C6\u56FE\u68C0\u7D22\u6570\u636E\u7684 <code>SELECT</code> \u8BED\u53E5\u4E2D\u4E5F\u542B\u6709 <code>ORDER BY</code> \u5B50\u53E5\uFF0C\u5219\u8BE5\u89C6\u56FE\u4E2D\u7684 <code>ORDER BY</code> \u5B50\u53E5\u5C06\u88AB\u8986\u76D6\u3002</p><h2 id="\u521B\u5EFA\u89C6\u56FE" tabindex="-1"><a class="header-anchor" href="#\u521B\u5EFA\u89C6\u56FE" aria-hidden="true">#</a> \u521B\u5EFA\u89C6\u56FE</h2><p>\u53EF\u4EE5\u4F7F\u7528 <code>CREATE VIEW</code> \u8BED\u53E5\u6765\u521B\u5EFA\u89C6\u56FE\u3002</p><p>\u8BED\u6CD5\u683C\u5F0F\u5982\u4E0B\uFF1A</p><blockquote><p>CREATE VIEW &lt;\u89C6\u56FE\u540D&gt; AS <code>&lt;SELECT \u8BED\u53E5&gt;</code></p></blockquote><p>\u8BED\u6CD5\u8BF4\u660E\u5982\u4E0B\u3002</p><ul><li><p><code>&lt;\u89C6\u56FE\u540D&gt;</code>\uFF1A\u6307\u5B9A\u89C6\u56FE\u7684\u540D\u79F0\u3002\u8BE5\u540D\u79F0\u5728\u6570\u636E\u5E93\u4E2D\u5FC5\u987B\u662F\u552F\u4E00\u7684\uFF0C\u4E0D\u80FD\u4E0E\u5176\u4ED6\u8868\u6216\u89C6\u56FE\u540C\u540D\u3002</p><p>\u5BF9\u4E8E\u521B\u5EFA\u89C6\u56FE\u4E2D\u7684 <code>SELECT</code> \u8BED\u53E5\u7684\u6307\u5B9A\u5B58\u5728\u4EE5\u4E0B\u9650\u5236\uFF1A</p></li><li><p>\u7528\u6237\u9664\u4E86\u62E5\u6709 <code>CREATE VIEW</code> \u6743\u9650\u5916\uFF0C\u8FD8\u5177\u6709\u64CD\u4F5C\u4E2D\u6D89\u53CA\u7684\u57FA\u7840\u8868\u548C\u5176\u4ED6\u89C6\u56FE\u7684\u76F8\u5173\u6743\u9650\u3002</p></li><li><p><code>SELECT</code> \u8BED\u53E5\u4E0D\u80FD\u5F15\u7528\u7CFB\u7EDF\u6216\u7528\u6237\u53D8\u91CF\u3002</p></li><li><p><code>SELECT</code> \u8BED\u53E5\u4E0D\u80FD\u5305\u542B <code>FROM</code> \u5B50\u53E5\u4E2D\u7684\u5B50\u67E5\u8BE2\u3002</p></li><li><p><code>SELECT</code> \u8BED\u53E5\u4E0D\u80FD\u5F15\u7528\u9884\u5904\u7406\u8BED\u53E5\u53C2\u6570\u3002</p></li><li><p><code>&lt;SELECT \u8BED\u53E5&gt;</code>\uFF1A\u6307\u5B9A\u521B\u5EFA\u89C6\u56FE\u7684 <code>SELECT</code> \u8BED\u53E5\uFF0C\u53EF\u7528\u4E8E\u67E5\u8BE2\u591A\u4E2A\u57FA\u7840\u8868\u6216\u6E90\u89C6\u56FE\u3002</p></li></ul><p>\u89C6\u56FE\u5B9A\u4E49\u4E2D\u5F15\u7528\u7684\u8868\u6216\u89C6\u56FE\u5FC5\u987B\u5B58\u5728\u3002\u4F46\u662F\uFF0C\u521B\u5EFA\u5B8C\u89C6\u56FE\u540E\uFF0C\u53EF\u4EE5\u5220\u9664\u5B9A\u4E49\u5F15\u7528\u7684\u8868\u6216\u89C6\u56FE\u3002\u53EF\u4F7F\u7528 <code>CHECK TABLE</code> \u8BED\u53E5\u68C0\u67E5\u89C6\u56FE\u5B9A\u4E49\u662F\u5426\u5B58\u5728\u8FD9\u7C7B\u95EE\u9898\u3002</p><p>\u89C6\u56FE\u5B9A\u4E49\u4E2D\u5141\u8BB8\u4F7F\u7528 <code>ORDER BY</code> \u8BED\u53E5\uFF0C\u4F46\u662F\u82E5\u4ECE\u7279\u5B9A\u89C6\u56FE\u8FDB\u884C\u9009\u62E9\uFF0C\u800C\u8BE5\u89C6\u56FE\u4F7F\u7528\u4E86\u81EA\u5DF1\u7684 <code>ORDER BY</code> \u8BED\u53E5\uFF0C\u5219\u89C6\u56FE\u5B9A\u4E49\u4E2D\u7684 <code>ORDER BY</code> \u5C06\u88AB\u5FFD\u7565\u3002</p><p>\u89C6\u56FE\u5B9A\u4E49\u4E2D\u4E0D\u80FD\u5F15\u7528 <code>TEMPORARY</code> \u8868\uFF08\u4E34\u65F6\u8868\uFF09\uFF0C\u4E0D\u80FD\u521B\u5EFA <code>TEMPORARY</code> \u89C6\u56FE\u3002</p><p><code>WITH CHECK OPTION</code> \u7684\u610F\u601D\u662F\uFF0C\u4FEE\u6539\u89C6\u56FE\u65F6\uFF0C\u68C0\u67E5\u63D2\u5165\u7684\u6570\u636E\u662F\u5426\u7B26\u5408 <code>WHERE</code> \u8BBE\u7F6E\u7684\u6761\u4EF6\u3002</p><h2 id="\u67E5\u8BE2\u89C6\u56FE" tabindex="-1"><a class="header-anchor" href="#\u67E5\u8BE2\u89C6\u56FE" aria-hidden="true">#</a> \u67E5\u8BE2\u89C6\u56FE</h2><p>\u89C6\u56FE\u4E00\u7ECF\u5B9A\u4E49\u4E4B\u540E\uFF0C\u5C31\u53EF\u4EE5\u5982\u540C\u67E5\u8BE2\u6570\u636E\u8868\u4E00\u6837\uFF0C\u4F7F\u7528 <code>SELECT</code> \u8BED\u53E5\u67E5\u8BE2\u89C6\u56FE\u4E2D\u7684\u6570\u636E\uFF0C\u8BED\u6CD5\u548C\u67E5\u8BE2\u57FA\u7840\u8868\u7684\u6570\u636E\u4E00\u6837\u3002</p><p>\u89C6\u56FE\u7528\u4E8E\u67E5\u8BE2\u4E3B\u8981\u5E94\u7528\u5728\u4EE5\u4E0B\u51E0\u4E2A\u65B9\u9762\uFF1A</p><ul><li>\u4F7F\u7528\u89C6\u56FE\u91CD\u65B0\u683C\u5F0F\u5316\u68C0\u7D22\u51FA\u7684\u6570\u636E\u3002</li><li>\u4F7F\u7528\u89C6\u56FE\u7B80\u5316\u590D\u6742\u7684\u8868\u8FDE\u63A5\u3002</li><li>\u4F7F\u7528\u89C6\u56FE\u8FC7\u6EE4\u6570\u636E\u3002</li></ul><p><code>DESCRIBE</code> \u53EF\u4EE5\u7528\u6765\u67E5\u770B\u89C6\u56FE\uFF0C\u8BED\u6CD5\u5982\u4E0B\uFF1A</p><blockquote><p>DESCRIBE \u89C6\u56FE\u540D\uFF1B</p></blockquote><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p><code>DESCRIBE</code> \u4E00\u822C\u60C5\u51B5\u4E0B\u53EF\u4EE5\u7B80\u5199\u6210 <code>DESC</code>\uFF0C\u8F93\u5165\u8FD9\u4E2A\u547D\u4EE4\u7684\u6267\u884C\u7ED3\u679C\u548C\u8F93\u5165 <code>DESCRIBE</code> \u662F\u4E00\u6837\u7684\u3002</p></div><ol><li><p>\u67E5\u770B\u89C6\u56FE</p><p>\u521B\u5EFA\u597D\u89C6\u56FE\u540E\uFF0C\u53EF\u4EE5\u901A\u8FC7\u67E5\u770B\u89C6\u56FE\u7684\u8BED\u53E5\u6765\u67E5\u770B\u89C6\u56FE\u7684\u5B57\u6BB5\u4FE1\u606F\u4EE5\u53CA\u8BE6\u7EC6\u4FE1\u606F\u3002</p></li><li><p>\u67E5\u770B\u89C6\u56FE\u7684\u5B57\u6BB5\u4FE1\u606F</p><p>\u67E5\u770B\u89C6\u56FE\u7684\u5B57\u6BB5\u4FE1\u606F\u4E0E\u67E5\u770B\u6570\u636E\u8868\u7684\u5B57\u6BB5\u4FE1\u606F\u4E00\u6837\uFF0C\u90FD\u662F\u4F7F\u7528 <code>DESCRIBE</code> \u5173\u952E\u5B57\u6765\u67E5\u770B\u7684\u3002\u5177\u4F53\u8BED\u6CD5\u5982\u4E0B\uFF1A</p><blockquote><p>DESCRIBE \u89C6\u56FE\u540D;</p></blockquote><p>\u6216\u7B80\u5199\u6210\uFF1A</p><blockquote><p>DESC \u89C6\u56FE\u540D;</p></blockquote></li></ol><h3 id="\u67E5\u770B\u89C6\u56FE\u7684\u8BE6\u7EC6\u4FE1\u606F" tabindex="-1"><a class="header-anchor" href="#\u67E5\u770B\u89C6\u56FE\u7684\u8BE6\u7EC6\u4FE1\u606F" aria-hidden="true">#</a> \u67E5\u770B\u89C6\u56FE\u7684\u8BE6\u7EC6\u4FE1\u606F</h3><p>\u5728 MySQL \u4E2D\uFF0C<code>SHOW CREATE VIEW</code> \u8BED\u53E5\u53EF\u4EE5\u67E5\u770B\u89C6\u56FE\u7684\u8BE6\u7EC6\u5B9A\u4E49\u3002\u5176\u8BED\u6CD5\u5982\u4E0B\u6240\u793A\uFF1A</p><blockquote><p>SHOW CREATE VIEW \u89C6\u56FE\u540D;</p></blockquote><p>\u901A\u8FC7\u4E0A\u9762\u7684\u8BED\u53E5\uFF0C\u8FD8\u53EF\u4EE5\u67E5\u770B\u521B\u5EFA\u89C6\u56FE\u7684\u8BED\u53E5\u3002\u521B\u5EFA\u89C6\u56FE\u7684\u8BED\u53E5\u53EF\u4EE5\u4F5C\u4E3A\u4FEE\u6539\u6216\u8005\u91CD\u65B0\u521B\u5EFA\u89C6\u56FE\u7684\u53C2\u8003\uFF0C\u65B9\u4FBF\u7528\u6237\u64CD\u4F5C\u3002</p><h2 id="\u4FEE\u6539\u89C6\u56FE" tabindex="-1"><a class="header-anchor" href="#\u4FEE\u6539\u89C6\u56FE" aria-hidden="true">#</a> \u4FEE\u6539\u89C6\u56FE</h2><p>\u4FEE\u6539\u89C6\u56FE\u662F\u6307\u4FEE\u6539 MySQL \u6570\u636E\u5E93\u4E2D\u5B58\u5728\u7684\u89C6\u56FE\uFF0C\u5F53\u57FA\u672C\u8868\u7684\u67D0\u4E9B\u5B57\u6BB5\u53D1\u751F\u53D8\u5316\u65F6\uFF0C\u53EF\u4EE5\u901A\u8FC7\u4FEE\u6539\u89C6\u56FE\u6765\u4FDD\u6301\u4E0E\u57FA\u672C\u8868\u7684\u4E00\u81F4\u6027\u3002</p><p><strong>\u57FA\u672C\u8BED\u6CD5\uFF1A</strong></p><p>\u53EF\u4EE5\u4F7F\u7528 <code>ALTER VIEW</code> \u8BED\u53E5\u6765\u5BF9\u5DF2\u6709\u7684\u89C6\u56FE\u8FDB\u884C\u4FEE\u6539\u3002</p><p>\u8BED\u6CD5\u683C\u5F0F\u5982\u4E0B\uFF1A</p><blockquote><p>ALTER VIEW &lt;\u89C6\u56FE\u540D&gt; AS <code>&lt;SELECT \u8BED\u53E5&gt;</code></p></blockquote><p>\u8BED\u6CD5\u8BF4\u660E\u5982\u4E0B\uFF1A</p><ul><li><p><code>&lt;\u89C6\u56FE\u540D&gt;</code>\uFF1A\u6307\u5B9A\u89C6\u56FE\u7684\u540D\u79F0\u3002\u8BE5\u540D\u79F0\u5728\u6570\u636E\u5E93\u4E2D\u5FC5\u987B\u662F\u552F\u4E00\u7684\uFF0C\u4E0D\u80FD\u4E0E\u5176\u4ED6\u8868\u6216\u89C6\u56FE\u540C\u540D\u3002</p></li><li><p><code>&lt;SELECT \u8BED\u53E5&gt;</code>\uFF1A\u6307\u5B9A\u521B\u5EFA\u89C6\u56FE\u7684 <code>SELECT</code> \u8BED\u53E5\uFF0C\u53EF\u7528\u4E8E\u67E5\u8BE2\u591A\u4E2A\u57FA\u7840\u8868\u6216\u6E90\u89C6\u56FE\u3002</p><p>\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5BF9\u4E8E <code>ALTER VIEW</code> \u8BED\u53E5\u7684\u4F7F\u7528\uFF0C\u9700\u8981\u7528\u6237\u5177\u6709\u9488\u5BF9\u89C6\u56FE\u7684 <code>CREATE VIEW</code> \u548C <code>DROP</code> \u6743\u9650\uFF0C\u4EE5\u53CA\u7531 <code>SELECT</code> \u8BED\u53E5\u9009\u62E9\u7684\u6BCF\u4E00\u5217\u4E0A\u7684\u67D0\u4E9B\u6743\u9650\u3002</p><p>\u4FEE\u6539\u89C6\u56FE\u7684\u5B9A\u4E49\uFF0C\u9664\u4E86\u53EF\u4EE5\u901A\u8FC7 <code>ALTER VIEW</code> \u5916\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u7528 <code>DROP VIEW</code> \u8BED\u53E5\u5148\u5220\u9664\u89C6\u56FE\uFF0C\u518D\u4F7F\u7528 <code>CREATE VIEW</code> \u8BED\u53E5\u6765\u5B9E\u73B0\u3002</p></li></ul><h3 id="\u4FEE\u6539\u89C6\u56FE\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u4FEE\u6539\u89C6\u56FE\u5185\u5BB9" aria-hidden="true">#</a> \u4FEE\u6539\u89C6\u56FE\u5185\u5BB9</h3><p>\u89C6\u56FE\u662F\u4E00\u4E2A\u865A\u62DF\u8868\uFF0C\u5B9E\u9645\u7684\u6570\u636E\u6765\u81EA\u4E8E\u57FA\u672C\u8868\uFF0C\u6240\u4EE5\u901A\u8FC7\u63D2\u5165\u3001\u4FEE\u6539\u548C\u5220\u9664\u64CD\u4F5C\u66F4\u65B0\u89C6\u56FE\u4E2D\u7684\u6570\u636E\uFF0C\u5B9E\u8D28\u4E0A\u662F\u5728\u66F4\u65B0\u89C6\u56FE\u6240\u5F15\u7528\u7684\u57FA\u672C\u8868\u7684\u6570\u636E\u3002</p><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u5BF9\u89C6\u56FE\u7684\u4FEE\u6539\u5C31\u662F\u5BF9\u57FA\u672C\u8868\u7684\u4FEE\u6539\uFF0C\u56E0\u6B64\u5728\u4FEE\u6539\u65F6\uFF0C\u8981\u6EE1\u8DB3\u57FA\u672C\u8868\u7684\u6570\u636E\u5B9A\u4E49\u3002</p></div><p>\u67D0\u4E9B\u89C6\u56FE\u662F\u53EF\u66F4\u65B0\u7684\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u53EF\u4EE5\u4F7F\u7528 <code>UPDATE</code>\u3001<code>DELETE</code> \u6216 <code>INSERT</code> \u7B49\u8BED\u53E5\u66F4\u65B0\u57FA\u672C\u8868\u7684\u5185\u5BB9\u3002\u5BF9\u4E8E\u53EF\u66F4\u65B0\u7684\u89C6\u56FE\uFF0C\u89C6\u56FE\u4E2D\u7684\u884C\u548C\u57FA\u672C\u8868\u7684\u884C\u4E4B\u95F4\u5FC5\u987B\u5177\u6709\u4E00\u5BF9\u4E00\u7684\u5173\u7CFB\u3002</p><p>\u8FD8\u6709\u4E00\u4E9B\u7279\u5B9A\u7684\u5176\u4ED6\u7ED3\u6784\uFF0C\u8FD9\u4E9B\u7ED3\u6784\u4F1A\u4F7F\u5F97\u89C6\u56FE\u4E0D\u53EF\u66F4\u65B0\u3002\u66F4\u5177\u4F53\u5730\u8BB2\uFF0C\u5982\u679C\u89C6\u56FE\u5305\u542B\u4EE5\u4E0B\u7ED3\u6784\u4E2D\u7684\u4EFB\u4F55\u4E00\u79CD\uFF0C\u5B83\u5C31\u662F\u4E0D\u53EF\u66F4\u65B0\u7684\uFF1A</p><ul><li><p>\u805A\u5408\u51FD\u6570 <code>SUM()</code>\u3001<code>MIN()</code>\u3001<code>MAX()</code>\u3001<code>COUNT()</code> \u7B49\u3002</p></li><li><p><code>DISTINCT</code> \u5173\u952E\u5B57\u3002</p></li><li><p><code>GROUP BY</code> \u5B50\u53E5\u3002</p></li><li><p><code>HAVING</code> \u5B50\u53E5\u3002</p></li><li><p><code>UNION</code> \u6216 <code>UNION ALL</code> \u8FD0\u7B97\u7B26\u3002</p></li><li><p>\u4F4D\u4E8E\u9009\u62E9\u5217\u8868\u4E2D\u7684\u5B50\u67E5\u8BE2\u3002</p></li><li><p><code>FROM</code> \u5B50\u53E5\u4E2D\u7684\u4E0D\u53EF\u66F4\u65B0\u89C6\u56FE\u6216\u5305\u542B\u591A\u4E2A\u8868\u3002</p></li><li><p><code>WHERE</code> \u5B50\u53E5\u4E2D\u7684\u5B50\u67E5\u8BE2\uFF0C\u5F15\u7528 <code>FROM</code> \u5B50\u53E5\u4E2D\u7684\u8868\u3002</p></li><li><p><code>ALGORITHM</code> \u9009\u9879\u4E3A <code>TEMPTABLE</code>\uFF08\u4F7F\u7528\u4E34\u65F6\u8868\u603B\u4F1A\u4F7F\u89C6\u56FE\u6210\u4E3A\u4E0D\u53EF\u66F4\u65B0\u7684\uFF09\u7684\u65F6\u5019\u3002</p></li></ul><h3 id="\u4FEE\u6539\u89C6\u56FE\u540D\u79F0" tabindex="-1"><a class="header-anchor" href="#\u4FEE\u6539\u89C6\u56FE\u540D\u79F0" aria-hidden="true">#</a> \u4FEE\u6539\u89C6\u56FE\u540D\u79F0</h3><p>\u4FEE\u6539\u89C6\u56FE\u7684\u540D\u79F0\u53EF\u4EE5\u5148\u5C06\u89C6\u56FE\u5220\u9664\uFF0C\u7136\u540E\u6309\u7167\u76F8\u540C\u7684\u5B9A\u4E49\u8BED\u53E5\u8FDB\u884C\u89C6\u56FE\u7684\u521B\u5EFA\uFF0C\u5E76\u547D\u540D\u4E3A\u65B0\u7684\u89C6\u56FE\u540D\u79F0\u3002</p><ol><li><p>\u5220\u9664\u89C6\u56FE</p><p>\u5220\u9664\u89C6\u56FE\u662F\u6307\u5220\u9664 MySQL \u6570\u636E\u5E93\u4E2D\u5DF2\u5B58\u5728\u7684\u89C6\u56FE\u3002\u5220\u9664\u89C6\u56FE\u65F6\uFF0C\u53EA\u80FD\u5220\u9664\u89C6\u56FE\u7684\u5B9A\u4E49\uFF0C\u4E0D\u4F1A\u5220\u9664\u6570\u636E\u3002</p><p><strong>\u57FA\u672C\u8BED\u6CD5\uFF1A</strong></p><p>\u53EF\u4EE5\u4F7F\u7528 <code>DROP VIEW</code> \u8BED\u53E5\u6765\u5220\u9664\u89C6\u56FE\u3002</p><p>\u8BED\u6CD5\u683C\u5F0F\u5982\u4E0B\uFF1A</p><blockquote><p>DROP VIEW &lt;\u89C6\u56FE\u540D 1&gt; [ , &lt;\u89C6\u56FE\u540D 2&gt; \u2026]</p></blockquote></li></ol><p>\u5176\u4E2D\uFF1A<code>&lt;\u89C6\u56FE\u540D&gt;</code>\u6307\u5B9A\u8981\u5220\u9664\u7684\u89C6\u56FE\u540D\u3002<code>DROP VIEW</code> \u8BED\u53E5\u53EF\u4EE5\u4E00\u6B21\u5220\u9664\u591A\u4E2A\u89C6\u56FE\uFF0C\u4F46\u662F\u5FC5\u987B\u5728\u6BCF\u4E2A\u89C6\u56FE\u4E0A\u62E5\u6709 <code>DROP</code> \u6743\u9650\u3002</p>',55),i=[l];function E(t,a){return o(),p("div",null,i)}const n=e(d,[["render",E],["__file","5. \u89C6\u56FE.html.vue"]]);export{n as default};
