import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as p,e as c}from"./app.e5be1224.js";const d={},a=c('<h1 id="_7-\u4E8B\u52A1" tabindex="-1"><a class="header-anchor" href="#_7-\u4E8B\u52A1" aria-hidden="true">#</a> 7. \u4E8B\u52A1</h1><p>\u5728\u94F6\u884C\u4E1A\u52A1\u4E2D\uFF0C\u6709\u4E00\u6761\u8BB0\u8D26\u539F\u5219\uFF0C\u5373\u6709\u501F\u6709\u8D37\uFF0C\u501F\u8D37\u76F8\u7B49\u3002\u4E3A\u4E86\u4FDD\u8BC1\u8FD9\u79CD\u539F\u5219\uFF0C\u6BCF\u53D1\u751F\u4E00\u7B14\u94F6\u884C\u4E1A\u52A1\uFF0C\u5C31\u5FC5\u987B\u786E\u4FDD\u4F1A\u8BA1\u8D26\u76EE\u4E0A\u501F\u65B9\u79D1\u76EE\u548C\u8D37\u65B9\u79D1\u76EE\u81F3\u5C11\u5404\u8BB0\u4E00\u7B14\uFF0C\u5E76\u4E14\u8FD9\u4E24\u7B14\u8D26\u8981\u4E48\u540C\u65F6\u6210\u529F\uFF0C\u8981\u4E48\u540C\u65F6\u5931\u8D25\u3002\u5982\u679C\u51FA\u73B0\u53EA\u8BB0\u5F55\u4E86\u501F\u65B9\u79D1\u76EE\uFF0C\u6216\u8005\u53EA\u8BB0\u5F55\u4E86\u8D37\u65B9\u79D1\u76EE\u7684\u60C5\u51B5\uFF0C\u5C31\u8FDD\u53CD\u4E86\u8BB0\u8D26\u539F\u5219\u3002\u4F1A\u51FA\u73B0\u8BB0\u9519\u8D26\u7684\u60C5\u51B5\u3002</p><p>\u5728\u94F6\u884C\u7684\u65E5\u5E38\u4E1A\u52A1\u4E2D\uFF0C\u53EA\u8981\u662F\u540C\u4E00\u94F6\u884C\uFF08\u5982\u90FD\u662F\u4E2D\u56FD\u519C\u4E1A\u94F6\u884C\uFF0C\u7B80\u79F0\u519C\u884C\uFF09\uFF0C\u4E00\u822C\u90FD\u652F\u6301\u8D26\u6237\u95F4\u7684\u76F4\u63A5\u8F6C\u8D26\u3002\u56E0\u6B64\uFF0C\u94F6\u884C\u8F6C\u8D26\u64CD\u4F5C\u5F80\u5F80\u4F1A\u6D89\u53CA\u4E24\u4E2A\u6216\u4E24\u4E2A\u4EE5\u4E0A\u7684\u8D26\u6237\u3002\u5728\u8F6C\u51FA\u8D26\u6237\u7684\u5B58\u6B3E\u51CF\u5C11\u4E00\u5B9A\u91D1\u989D\u7684\u540C\u65F6\uFF0C\u8F6C\u5165\u8D26\u6237\u7684\u5B58\u6B3E\u5C31\u8981\u589E\u52A0\u76F8\u5E94\u7684\u91D1\u989D\u3002</p><p>\u4E8B\u52A1\u53EF\u4EE5\u5C06\u4E00\u7CFB\u5217\u7684\u6570\u636E\u64CD\u4F5C\u6346\u7ED1\u6210\u4E00\u4E2A\u6574\u4F53\u8FDB\u884C\u7EDF\u4E00\u7BA1\u7406\uFF0C\u5982\u679C\u67D0\u4E00\u4E8B\u52A1\u6267\u884C\u6210\u529F\uFF0C\u5219\u5728\u8BE5\u4E8B\u52A1\u4E2D\u8FDB\u884C\u7684\u6240\u6709\u6570\u636E\u66F4\u6539\u5747\u4F1A\u63D0\u4EA4\uFF0C\u6210\u4E3A\u6570\u636E\u5E93\u4E2D\u7684\u6C38\u4E45\u7EC4\u6210\u90E8\u5206\u3002\u5982\u679C\u4E8B\u52A1\u6267\u884C\u65F6\u9047\u5230\u9519\u8BEF\uFF0C\u5219\u5C31\u5FC5\u987B\u53D6\u6D88\u6216\u56DE\u6EDA\u3002\u53D6\u6D88\u6216\u56DE\u6EDA\u540E\uFF0C\u6570\u636E\u5C06\u5168\u90E8\u6062\u590D\u5230\u64CD\u4F5C\u524D\u7684\u72B6\u6001\uFF0C\u6240\u6709\u6570\u636E\u7684\u66F4\u6539\u5747\u88AB\u6E05\u9664\u3002</p><p>MySQL \u901A\u8FC7\u4E8B\u52A1\u4FDD\u8BC1\u4E86\u6570\u636E\u7684\u4E00\u81F4\u6027\u3002\u4E0A\u8FF0\u63D0\u5230\u7684\u8F6C\u8D26\u8FC7\u7A0B\u5C31\u662F\u4E00\u4E2A\u4E8B\u52A1\uFF0C\u5B83\u9700\u8981\u4E24\u6761 UPDATE \u8BED\u53E5\u6765\u5B8C\u6210\u3002\u8FD9\u4E24\u6761\u8BED\u53E5\u662F\u4E00\u4E2A\u6574\u4F53\uFF0C\u5982\u679C\u5176\u4E2D\u4EFB\u4F55\u4E00\u4E2A\u73AF\u8282\u51FA\u73B0\u95EE\u9898\uFF0C\u5219\u6574\u4E2A\u8F6C\u8D26\u4E1A\u52A1\u4E5F\u5E94\u53D6\u6D88\uFF0C\u4E24\u4E2A\u8D26\u6237\u4E2D\u7684\u4F59\u989D\u5E94\u6062\u590D\u4E3A\u539F\u6765\u7684\u6570\u636E\uFF0C\u4ECE\u800C\u786E\u4FDD\u8F6C\u8D26\u524D\u548C\u8F6C\u8D26\u540E\u7684\u4F59\u989D\u603B\u548C\u4E0D\u53D8\u3002</p><h2 id="\u4E8B\u52A1\u7684\u57FA\u672C\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u52A1\u7684\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a> \u4E8B\u52A1\u7684\u57FA\u672C\u6982\u5FF5</h2><p>\u6570\u636E\u5E93\u7684**\u4E8B\u52A1\uFF08Transaction\uFF09**\u662F\u4E00\u79CD\u673A\u5236\u3001\u4E00\u4E2A\u64CD\u4F5C\u5E8F\u5217\uFF0C\u5305\u542B\u4E86\u4E00\u7EC4\u6570\u636E\u5E93\u64CD\u4F5C\u547D\u4EE4\u3002\u4E8B\u52A1\u628A\u6240\u6709\u7684\u547D\u4EE4\u4F5C\u4E3A\u4E00\u4E2A\u6574\u4F53\u4E00\u8D77\u5411\u7CFB\u7EDF\u63D0\u4EA4\u6216\u64A4\u9500\u64CD\u4F5C\u8BF7\u6C42\uFF0C\u5373\u8FD9\u4E00\u7EC4\u6570\u636E\u5E93\u547D\u4EE4\u8981\u4E48\u90FD\u6267\u884C\uFF0C\u8981\u4E48\u90FD\u4E0D\u6267\u884C\uFF0C\u56E0\u6B64\u4E8B\u52A1\u662F\u4E00\u4E2A\u4E0D\u53EF\u5206\u5272\u7684\u5DE5\u4F5C\u903B\u8F91\u5355\u5143\u3002</p><p>\u5728\u6570\u636E\u5E93\u7CFB\u7EDF\u4E0A\u6267\u884C\u5E76\u53D1\u64CD\u4F5C\u65F6\uFF0C\u4E8B\u52A1\u662F\u4F5C\u4E3A\u6700\u5C0F\u7684\u63A7\u5236\u5355\u5143\u6765\u4F7F\u7528\u7684\uFF0C\u7279\u522B\u9002\u7528\u4E8E\u591A\u7528\u6237\u540C\u65F6\u64CD\u4F5C\u7684\u6570\u636E\u5E93\u7CFB\u7EDF\u3002\u4F8B\u5982\uFF0C\u822A\u7A7A\u516C\u53F8\u7684\u8BA2\u7968\u7CFB\u7EDF\u3001\u94F6\u884C\u3001\u4FDD\u9669\u516C\u53F8\u4EE5\u53CA\u8BC1\u5238\u4EA4\u6613\u7CFB\u7EDF\u7B49\u3002</p><p>\u4E8B\u52A1\u5177\u6709 4 \u4E2A\u7279\u6027\uFF0C\u5373\u539F\u5B50\u6027\uFF08Atomicity\uFF09\u3001\u4E00\u81F4\u6027\uFF08Consistency\uFF09\u3001\u9694\u79BB\u6027\uFF08Isolation\uFF09\u548C\u6301\u4E45\u6027\uFF08Durability\uFF09\uFF0C\u8FD9 4 \u4E2A\u7279\u6027\u901A\u5E38\u7B80\u79F0\u4E3A ACID\u3002</p><ol><li><p>\u539F\u5B50\u6027</p><p>\u4E8B\u52A1\u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u64CD\u4F5C\u3002\u4E8B\u52A1\u7684\u5404\u5143\u7D20\u662F\u4E0D\u53EF\u5206\u7684\uFF08\u539F\u5B50\u7684\uFF09\u3002\u4E8B\u52A1\u4E2D\u7684\u6240\u6709\u5143\u7D20\u5FC5\u987B\u4F5C\u4E3A\u4E00\u4E2A\u6574\u4F53\u63D0\u4EA4\u6216\u56DE\u6EDA\u3002\u5982\u679C\u4E8B\u52A1\u4E2D\u7684\u4EFB\u4F55\u5143\u7D20\u5931\u8D25\uFF0C\u5219\u6574\u4E2A\u4E8B\u52A1\u5C06\u5931\u8D25\u3002</p><p>\u4EE5\u94F6\u884C\u8F6C\u8D26\u4E8B\u52A1\u4E3A\u4F8B\uFF0C\u5982\u679C\u8BE5\u4E8B\u52A1\u63D0\u4EA4\u4E86\uFF0C\u5219\u8FD9\u4E24\u4E2A\u8D26\u6237\u7684\u6570\u636E\u5C06\u4F1A\u66F4\u65B0\u3002\u5982\u679C\u7531\u4E8E\u67D0\u79CD\u539F\u56E0\uFF0C\u4E8B\u52A1\u5728\u6210\u529F\u66F4\u65B0\u8FD9\u4E24\u4E2A\u8D26\u6237\u4E4B\u524D\u7EC8\u6B62\u4E86\uFF0C\u5219\u4E0D\u4F1A\u66F4\u65B0\u8FD9\u4E24\u4E2A\u8D26\u6237\u7684\u4F59\u989D\uFF0C\u5E76\u4E14\u4F1A\u64A4\u9500\u5BF9\u4EFB\u4F55\u8D26\u6237\u4F59\u989D\u7684\u4FEE\u6539\uFF0C\u4E8B\u52A1\u4E0D\u80FD\u90E8\u5206\u63D0\u4EA4\u3002</p></li><li><p>\u4E00\u81F4\u6027</p><p>\u5F53\u4E8B\u52A1\u5B8C\u6210\u65F6\uFF0C\u6570\u636E\u5FC5\u987B\u5904\u4E8E\u4E00\u81F4\u72B6\u6001\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5728\u4E8B\u52A1\u5F00\u59CB\u4E4B\u524D\uFF0C\u6570\u636E\u5E93\u4E2D\u5B58\u50A8\u7684\u6570\u636E\u5904\u4E8E\u4E00\u81F4\u72B6\u6001\u3002\u5728\u6B63\u5728\u8FDB\u884C\u7684\u4E8B\u52A1\u4E2D. \u6570\u636E\u53EF\u80FD\u5904\u4E8E\u4E0D\u4E00\u81F4\u7684\u72B6\u6001\uFF0C\u5982\u6570\u636E\u53EF\u80FD\u6709\u90E8\u5206\u88AB\u4FEE\u6539\u3002\u7136\u800C\uFF0C\u5F53\u4E8B\u52A1\u6210\u529F\u5B8C\u6210\u65F6\uFF0C\u6570\u636E\u5FC5\u987B\u518D\u6B21\u56DE\u5230\u5DF2\u77E5\u7684\u4E00\u81F4\u72B6\u6001\u3002\u901A\u8FC7\u4E8B\u52A1\u5BF9\u6570\u636E\u6240\u505A\u7684\u4FEE\u6539\u4E0D\u80FD\u635F\u574F\u6570\u636E\uFF0C\u6216\u8005\u8BF4\u4E8B\u52A1\u4E0D\u80FD\u4F7F\u6570\u636E\u5B58\u50A8\u5904\u4E8E\u4E0D\u7A33\u5B9A\u7684\u72B6\u6001\u3002</p><p>\u4EE5\u94F6\u884C\u8F6C\u8D26\u4E8B\u52A1\u4E8B\u52A1\u4E3A\u4F8B\u3002\u5728\u4E8B\u52A1\u5F00\u59CB\u4E4B\u524D\uFF0C\u6240\u6709\u8D26\u6237\u4F59\u989D\u7684\u603B\u989D\u5904\u4E8E\u4E00\u81F4\u72B6\u6001\u3002\u5728\u4E8B\u52A1\u8FDB\u884C\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u4E00\u4E2A\u8D26\u6237\u4F59\u989D\u51CF\u5C11\u4E86\uFF0C\u800C\u53E6\u4E00\u4E2A\u8D26\u6237\u4F59\u989D\u5C1A\u672A\u4FEE\u6539\u3002\u56E0\u6B64\uFF0C\u6240\u6709\u8D26\u6237\u4F59\u989D\u7684\u603B\u989D\u5904\u4E8E\u4E0D\u4E00\u81F4\u72B6\u6001\u3002\u4E8B\u52A1\u5B8C\u6210\u4EE5\u540E\uFF0C\u8D26\u6237\u4F59\u989D\u7684\u603B\u989D\u518D\u6B21\u6062\u590D\u5230\u4E00\u81F4\u72B6\u6001\u3002</p></li><li><p>\u9694\u79BB\u6027</p><p>\u5BF9\u6570\u636E\u8FDB\u884C\u4FEE\u6539\u7684\u6240\u6709\u5E76\u53D1\u4E8B\u52A1\u662F\u5F7C\u6B64\u9694\u79BB\u7684\uFF0C\u8FD9\u8868\u660E\u4E8B\u52A1\u5FC5\u987B\u662F\u72EC\u7ACB\u7684\uFF0C\u5B83\u4E0D\u5E94\u4EE5\u4EFB\u4F55\u65B9\u5F0F\u4F9D\u8D56\u4E8E\u6216\u5F71\u54CD\u5176\u4ED6\u4E8B\u52A1\u3002\u4FEE\u6539\u6570\u636E\u7684\u4E8B\u52A1\u53EF\u4EE5\u5728\u53E6\u4E00\u4E2A\u4F7F\u7528\u76F8\u540C\u6570\u636E\u7684\u4E8B\u52A1\u5F00\u59CB\u4E4B\u524D\u8BBF\u95EE\u8FD9\u4E9B\u6570\u636E\uFF0C\u6216\u8005\u5728\u53E6\u4E00\u4E2A\u4F7F\u7528\u76F8\u540C\u6570\u636E\u7684\u4E8B\u52A1\u7ED3\u675F\u4E4B\u540E\u8BBF\u95EE\u8FD9\u4E9B\u6570\u636E\u3002</p><p>\u53E6\u5916\uFF0C\u5F53\u4E8B\u52A1\u4FEE\u6539\u6570\u636E\u65F6\uFF0C\u5982\u679C\u4EFB\u4F55\u5176\u4ED6\u8FDB\u7A0B\u6B63\u5728\u540C\u65F6\u4F7F\u7528\u76F8\u540C\u7684\u6570\u636E\uFF0C\u5219\u76F4\u5230\u8BE5\u4E8B\u52A1\u6210\u529F\u63D0\u4EA4\u4E4B\u540E\uFF0C\u5BF9\u6570\u636E\u7684\u4FEE\u6539\u624D\u80FD\u751F\u6548\u3002\u5F20\u4E09\u548C\u674E\u56DB\u4E4B\u95F4\u7684\u8F6C\u8D26\u4E0E\u738B\u4E94\u548C\u8D75\u4E8C\u4E4B\u95F4\u7684\u8F6C\u8D26\uFF0C\u6C38\u8FDC\u662F\u76F8\u4E92\u72EC\u7ACB\u7684\u3002</p></li><li><p>\u6301\u4E45\u6027</p><p>\u4E8B\u52A1\u7684\u6301\u4E45\u6027\u6307\u4E0D\u7BA1\u7CFB\u7EDF\u662F\u5426\u53D1\u751F\u4E86\u6545\u969C\uFF0C\u4E8B\u52A1\u5904\u7406\u7684\u7ED3\u679C\u90FD\u662F\u6C38\u4E45\u7684\u3002</p><p>\u4E00\u4E2A\u4E8B\u52A1\u6210\u529F\u5B8C\u6210\u4E4B\u540E\uFF0C\u5B83\u5BF9\u6570\u636E\u5E93\u6240\u4F5C\u7684\u6539\u53D8\u662F\u6C38\u4E45\u6027\u7684\uFF0C\u5373\u4F7F\u7CFB\u7EDF\u51FA\u73B0\u6545\u969C\u4E5F\u662F\u5982\u6B64\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4E00\u65E6\u4E8B\u52A1\u88AB\u63D0\u4EA4\uFF0C\u4E8B\u52A1\u5BF9\u6570\u636E\u6240\u505A\u7684\u4EFB\u4F55\u53D8\u52A8\u90FD\u4F1A\u88AB\u6C38\u4E45\u5730\u4FDD\u7559\u5728\u6570\u636E\u5E93\u4E2D\u3002</p><p>\u4E8B\u52A1\u7684 ACID \u539F\u5219\u4FDD\u8BC1\u4E86\u4E00\u4E2A\u4E8B\u52A1\u6216\u8005\u6210\u529F\u63D0\u4EA4\uFF0C\u6216\u8005\u5931\u8D25\u56DE\u6EDA\uFF0C\u4E8C\u8005\u5FC5\u5C45\u5176\u4E00\u3002\u56E0\u6B64\uFF0C\u5B83\u5BF9\u4E8B\u52A1\u7684\u4FEE\u6539\u5177\u6709\u53EF\u6062\u590D\u6027\u3002\u5373\u5F53\u4E8B\u52A1\u5931\u8D25\u65F6\uFF0C\u5B83\u5BF9\u6570\u636E\u7684\u4FEE\u6539\u90FD\u4F1A\u6062\u590D\u5230\u8BE5\u4E8B\u52A1\u6267\u884C\u524D\u7684\u72B6\u6001\u3002</p></li></ol><h2 id="\u4E8B\u52A1\u63D0\u4EA4\u4E0E\u56DE\u6EDA" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u52A1\u63D0\u4EA4\u4E0E\u56DE\u6EDA" aria-hidden="true">#</a> \u4E8B\u52A1\u63D0\u4EA4\u4E0E\u56DE\u6EDA</h2><p>SQL \u4F7F\u7528\u4E0B\u5217\u8BED\u53E5\u6765\u7BA1\u7406\u4E8B\u52A1\u3002</p><ol><li><p>\u5F00\u59CB\u4E8B\u52A1</p><p><code>BEGIN;</code>\u6216<code>START TRANSACTION;</code></p><p>\u8FD9\u4E2A\u8BED\u53E5\u663E\u5F0F\u5730\u6807\u8BB0\u4E00\u4E2A\u4E8B\u52A1\u7684\u8D77\u59CB\u70B9\u3002</p></li><li><p>\u63D0\u4EA4\u4E8B\u52A1</p><p>MySQL \u4F7F\u7528\u4E0B\u9762\u7684\u8BED\u53E5\u6765\u663E\u793A\u63D0\u4EA4\u4E8B\u52A1\uFF1A<code>COMMIT</code>;</p><p><code>COMMIT</code> \u8868\u793A\u63D0\u4EA4\u4E8B\u52A1\uFF0C\u5373\u63D0\u4EA4\u4E8B\u52A1\u7684\u6240\u6709\u64CD\u4F5C\uFF0C\u5177\u4F53\u5730\u8BF4\uFF0C\u5C31\u662F\u5C06\u4E8B\u52A1\u4E2D\u6240\u6709\u5BF9\u6570\u636E\u5E93\u7684\u66F4\u65B0\u90FD\u5199\u5230\u78C1\u76D8\u4E0A\u7684\u7269\u7406\u6570\u636E\u5E93\u4E2D\uFF0C\u4E8B\u52A1\u6B63\u5E38\u7ED3\u675F\u3002</p><p>\u63D0\u4EA4\u4E8B\u52A1\uFF0C\u610F\u5473\u7740\u5C06\u4E8B\u52A1\u5F00\u59CB\u4EE5\u6765\u6240\u6267\u884C\u7684\u6240\u6709\u6570\u636E\u90FD\u4FEE\u6539\u6210\u4E3A\u6570\u636E\u5E93\u7684\u6C38\u4E45\u90E8\u5206\uFF0C\u56E0\u6B64\u4E5F\u6807\u5FD7\u7740\u4E00\u4E2A\u4E8B\u52A1\u7684\u7ED3\u675F\u3002\u4E00\u65E6\u6267\u884C\u4E86\u8BE5\u547D\u4EE4\uFF0C\u5C06\u4E0D\u80FD\u56DE\u6EDA\u4E8B\u52A1\u3002\u53EA\u6709\u5728\u6240\u6709\u4FEE\u6539\u90FD\u51C6\u5907\u597D\u63D0\u4EA4\u7ED9\u6570\u636E\u5E93\u65F6\uFF0C\u624D\u6267\u884C\u8FD9\u4E00\u64CD\u4F5C\u3002</p><p>\u5728 MySQL \u4E2D\uFF0C\u66F4\u65B0\u8BED\u53E5\u3001\u6570\u636E\u5B9A\u4E49\u8BED\u53E5\u548C\u6743\u9650\u7BA1\u7406\u8BED\u53E5\u662F\u9690\u5F0F\u63D0\u4EA4\u7684\u3002</p><p>\u66F4\u65B0\u8BED\u53E5\u6709\uFF1A<code>BEGIN</code>\u3001<code>START TRANSACTION</code>\u3001<code>RENAME</code> <code>TABLE</code>\u3001<code>TRUNCATE TABLE</code> \u7B49\u3002</p><p>\u6570\u636E\u5B9A\u4E49\u8BED\u53E5\uFF08CREATE\u3001ALTER\u3001DROP\uFF09\u6709\uFF1A<code>CREATE DATABASE</code>\u3001<code>ALTER TABLE</code>\u3001<code>DROP VIEW</code> \u7B49\u3002\u6743\u9650\u7BA1\u7406\u548C\u8D26\u6237\u7BA1\u7406\u8BED\u53E5\uFF1A<code>GRANT</code>\u3001<code>SET PASSWORD</code>\u3001<code>CREATE USER</code>\u3001<code>DROP USER</code> \u7B49\u3002</p></li><li><p>\u56DE\u6EDA\uFF08\u64A4\u9500\uFF09\u4E8B\u52A1</p><p>\u5148\u5173\u95ED MySQL \u7684\u81EA\u52A8\u63D0\u4EA4\u529F\u80FD\uFF1A<code>SET AUTOCOMMIT = 0;</code></p><p>MySQL \u56DE\u6EDA\u4E8B\u52A1\uFF1A<code>ROLLBACK;</code></p><p><code>ROLLBACK</code> \u8868\u793A\u64A4\u9500\u4E8B\u52A1\uFF0C\u5373\u5728\u4E8B\u52A1\u8FD0\u884C\u7684\u8FC7\u7A0B\u4E2D\u53D1\u751F\u4E86\u67D0\u79CD\u6545\u969C\uFF0C\u4E8B\u52A1\u4E0D\u80FD\u7EE7\u7EED\u6267\u884C\uFF0C\u7CFB\u7EDF\u5C06\u4E8B\u52A1\u4E2D\u5BF9\u6570\u636E\u5E93\u7684\u6240\u6709\u5DF2\u5B8C\u6210\u7684\u64CD\u4F5C\u5168\u90E8\u64A4\u9500\uFF0C\u56DE\u6EDA\u5230\u4E8B\u52A1\u5F00\u59CB\u65F6\u7684\u72B6\u6001\u3002\u8FD9\u91CC\u7684\u64CD\u4F5C\u6307\u5BF9\u6570\u636E\u5E93\u7684\u66F4\u65B0\u64CD\u4F5C\u3002</p><p>\u5F53\u4E8B\u52A1\u6267\u884C\u8FC7\u7A0B\u4E2D\u9047\u5230\u9519\u8BEF\u65F6\uFF0C\u4F7F\u7528 <code>ROLLBACK</code> \u8BED\u53E5\u4F7F\u4E8B\u52A1\u56DE\u6EDA\u5230\u8D77\u70B9\u6216\u6307\u5B9A\u7684\u4FDD\u6301\u70B9\u5904\u3002\u540C\u65F6\uFF0C\u7CFB\u7EDF\u5C06\u6E05\u9664\u81EA\u4E8B\u52A1\u8D77\u70B9\u6216\u5230\u67D0\u4E2A\u4FDD\u5B58\u70B9\u6240\u505A\u7684\u6240\u6709\u7684\u6570\u636E\u4FEE\u6539\uFF0C\u5E76\u4E14\u91CA\u653E\u7531\u4E8B\u52A1\u63A7\u5236\u7684\u8D44\u6E90\u3002\u56E0\u6B64\uFF0C\u8FD9\u6761\u8BED\u53E5\u4E5F\u6807\u5FD7\u7740\u4E8B\u52A1\u7684\u7ED3\u675F\u3002</p></li></ol><h2 id="\u4FDD\u5B58\u70B9" tabindex="-1"><a class="header-anchor" href="#\u4FDD\u5B58\u70B9" aria-hidden="true">#</a> \u4FDD\u5B58\u70B9</h2><p>\u4FDD\u5B58\u70B9\u53EF\u4EE5\u5B9E\u73B0\u4E8B\u52A1\u7684\u201C\u90E8\u5206\u201D\u63D0\u4EA4\u6216\u90E8\u5206\u56DE\u6EDA\u3002\u53EF\u4EE5\u4F7F\u7528<code>SAVEPOINT \u4FDD\u5B58\u70B9\u540D;</code>\u8BED\u53E5\u521B\u5EFA\u4FDD\u5B58\u70B9\u3002\u7136\u540E\u6267\u884C<code>ROLLBACK TO \u4FDD\u5B58\u70B9\u540D;</code>\u8BED\u53E5\u56DE\u6EDA\u5230\u5DF2\u7ECF\u8BBE\u7F6E\u597D\u7684\u4FDD\u5B58\u70B9\u3002\u56DE\u6EDA\u5230\u4FDD\u5B58\u70B9\u540E\u4E0D\u4F1A\u81EA\u52A8\u63D0\u4EA4\uFF0C\u4E8B\u52A1\u7ED3\u675F\u5FC5\u987B\u4F7F\u7528 <code>COMMIT</code> \u6216 <code>ROLLBACK</code> \u547D\u4EE4\u3002</p>',15),i=[a];function r(t,l){return o(),p("div",null,i)}const n=e(d,[["render",r],["__file","7. \u4E8B\u52A1.html.vue"]]);export{n as default};
